document.addEventListener("DOMContentLoaded",function(){const t=document.getElementById("donate-button"),n=document.getElementById("modal"),o=document.getElementById("modal-content");t.addEventListener("click",function(){setTimeout(function(){n.style.display="block",t.style.display="none",document.body.style.overflow="hidden"},250)}),window.addEventListener("click",function(e){e.target!==o&&e.target!==t&&(n.style.display="none",t.style.display="block",document.body.style.overflow="auto")})});